async function DTW_getPosts(t){const n=`https://dev.to/api/articles?username=${t}`,e=await fetch(n);return await e.json()}async function DTW_showPosts(t){let n;const e=t.getAttribute("data-n"),s=t.getAttribute("data-username");try{n=await DTW_getPosts(s)}catch(n){return console.log(n),t.innerHTML="<p> <span style = 'color: red'>Something bad has happened :( </span></p>"}t.innerHTML="";let a=0;for(let s of n){if(a>=e)break;const n=`\n    <div class="DTW-container__post">\n      <div class="post__title">\n        <h5><a href="${s.url}">${s.title}</a></h5>\n      </div>\n      <div class="post__content">\n        <p class="content__date">${new Date(s.published_at).toDateString()}</p>\n        <a href="${s.url}">\n          <figure class="content__likes">\n            <p>${s.positive_reactions_count}</p>\n            <img src="${DTW_image_reactions}">\n          </figure>\n        </a>\n        <a href="https://dev.to${s.path}#comments">\n          <figure class="content__comments">\n            <p>${s.comments_count}</p>\n            <img src="${DTW_image_comments}">\n          </figure>\n        </a>\n      </div>\n    </div>\n    `;t.innerHTML+=n,a++}}const DTW_containers=document.querySelectorAll(".DTW-container");DTW_containers.forEach(t=>{DTW_showPosts(t)});
